package Demo;import com.alibaba.fastjson.JSON;import com.alibaba.fastjson.JSONObject;import com.oscar.dispatcher.util.StringUtil;import java.io.*;public class Serilaziable {    public static void main(String[] args) throws IOException,ClassNotFoundException {        JSONObject jsonSource=new JSONObject();        jsonSource.put("ceshi","测试");        ByteArrayOutputStream baos = new ByteArrayOutputStream();        ObjectOutputStream oos = new ObjectOutputStream(baos);        oos.writeObject(jsonSource);        byte[] sByte=baos.toByteArray();        ByteArrayInputStream byteArrInputStream=new ByteArrayInputStream(sByte);        ObjectInputStream objInputStr=new ObjectInputStream(byteArrInputStream);        com.alibaba.fastjson.JSONObject jsonObj=(com.alibaba.fastjson.JSONObject)objInputStr.readObject();        System.out.println(jsonObj.toString());    }}