package learning;public class ThreadTest extends Thread{    private Car car;    ThreadTest(Car car){        this.car=car;    }    @Override    public void run(){        System.out.println(Thread.currentThread().getId()+"   验证开始！");        for(int i=0;i<10000;i++){            System.out.println(Thread.currentThread().getId()+"  "+String.valueOf(car==Car.getInstance()));            if(car!=Car.getInstance()){                throw new RuntimeException("单例模式失效了！");            }        }        System.out.println(Thread.currentThread().getId()+"  验证结束！");    }}