package learning.sort;import java.util.ArrayList;import java.util.HashSet;import java.util.Set;public class sortTest {    public static void main(String[] args){        int[] l=new int[]{2,3,5};        sort1(l);        for(int i=0;i<l.length;i++){            System.out.println(l[i]);        }    }    public static final void sort1(int[] cache){        int size=cache.length;        for(int i=0;i<size;i++){            for(int j=0;j<size-1-i;j++){                if(cache[j]<cache[j+1]){                    int temp=cache[j];                    cache[j]=cache[j+1];                    cache[j+1]=temp;                }            }        }    }    private static final void sort(int[] target){        int size=target.length;        for(int i=0;i<size-1;i++){            for(int j=0;j<size-1-i;j++){                if(target[j]<target[j+1]){                    int temp=target[j];                    target[j]=target[j+1];                    target[j+1]=temp;                }            }        }    }    public static final void sort(Object[] l) {        int size = l.length;        for (int i = 0; i < size; i++) {            for (int j = i; j < size - 1; j++) {                int now = (Integer) l[j];                int next = (Integer) l[j + 1];                if (now > next) {                    l[j] = next;                    l[j + 1] = now;                }            }        }    }}