package socketDemo;import java.io.IOException;import java.io.Reader;import java.io.Writer;import java.net.Socket;public class IOTools {    public static void closeInputStream(Reader i) {        if (i == null) {            return;        }        try {            i.close();        } catch (IOException e) {            e.printStackTrace();        }    }    public static void closeOutputStream(Writer o) {        if (o == null) {            return;        }        try {            o.flush();            o.close();        } catch (IOException e) {            e.printStackTrace();        }    }    public static void closeSocket(Socket socket){        if(socket==null){            return;        }        try{            socket.close();        }catch (IOException e){            e.printStackTrace();        }    }}